'use strict'
const findDB = require('./find-db.js')
const fetchData = require('./fetch-data.js')

module.exports = function fakeDataFetch (fakeContent, callback) {
  findDB(fakeContent, function (err, db) {
    if (err) return callback(err, null)
    fetchData(db, function (err, output) {
      callback(err, output)
    })
  })
}
